mapper.select:
SELECT cn.notification_id, cn.user_id, cn.sender_id, cn.type, cn.content, cn.link, cn.is_read, cn.create_time, cn.read_time, cu1.username AS username, cui1.avatar AS userAvatar, cu2.username AS senderName, cui2.avatar AS senderAvatar FROM club_notification cn LEFT JOIN club_user cu1 ON cn.user_id = cu1.user_id LEFT JOIN club_user cu2 ON cn.sender_id = cu2.user_id LEFT JOIN club_user_info cui1 ON cu1.user_id = cui1.user_id LEFT JOIN club_user_info cui2 ON cu2.user_id = cui2.user_id WHERE cn.user_id = #{userId}
and cn.type in ('register_welcome', 'register_init_pwd' , 'following_user')
and cn.type = #{type}
and cn.is_read = #{isRead}
ORDER BY cn.create_time DESC

mapper.update:
update club_notification
set is_read = #{isRead}

  , read_time =  now()

where notification_id = #{notificationId} and user_id = #{userId}
