if(ele.value == value){\n                    arr[idx][\'region\'] = ele.label\n                }\n            })\n        }\n        if(type == 3){\n            arr[idx][\'shelvesTypeKey\'] = value\n            storeShelvesListshelvesType.forEach(ele=&gt;{\n                if(ele.value == value){\n                    arr[idx][\'shelvesType\'] = ele.label\n                }\n            })\n        }\n        settdata([...arr])\n    }\n\n  const defaultColumns: ColumnType&lt;any&gt;[] = [\n    {\n      title:\'品牌\',\n      dataIndex: \'brand\',\n            render: (text, record,index) =&gt; (\n                                &lt;Select style={{width:\"150px\"}}\n                                    defaultValue={record.brandKey}\n                                                            onChange={(value)=&gt;onCellChange(value,1,index)}\n                                    options={storeShelvesListbrand}\n                                /&gt;\n                            )\n\n    },\n        {\n          title:\'区域\',\n          dataIndex: \'region\',\n            render: (text, record,index) =&gt; (\n                                &lt;Select style={{width:\"150px\"}}\n                                    defaultValue={record.regionKey}\n                                                            onChange={(value)=&gt;onCellChange(value,2,index)}\n                                    options={storeShelvesListregion}\n                                /&gt;\n                            )\n        },\n        {\n          title:\'器架类型\',\n          dataIndex: \'shelvesType\',\n            render: (text, record,index) =&gt; (\n                                &lt;Select style={{width:\"150px\"}}\n                                    defaultValue={record.shelvesTypeKey}\n                                                            onChange={(value)=&gt;onCellChange(value,3,index)}\n                                    options={storeShelvesListshelvesType}\n                                /&gt;\n                            )\n        },\n        {\n          title:\'数量\',\n          dataIndex: \'num\',\n            render: (text, record,index) =&gt; (\n                                &lt;Input style={{width:\"150px\"}}\n                                    value={record.num}\n                                                            onChange={(e)=&gt;onCellChange(e,3,index)}\n                                /&gt;\n                            )\n        },\n        {\n          title:\'操作\',\n          dataIndex: \'action\',\n            render: (_, record) =&gt; (\n                &lt;&gt;\n                &lt;Popconfirm\n                    title={\'确定删除吗\'}\n                    okText={\'确定\'}\n                    cancelText={\'取消\'}\n                    onConfirm={() =&gt; handleDelete(record.id)}\n                &gt;\n                    &lt;Button type=\"link\" danger&gt;\n                      删除\n                    &lt;/Button&gt;\n                &lt;/Popconfirm&gt;\n\n                &lt;/&gt;\n            ),\n        }\n  ]\n\n  const [columns, columnsHandle] = useSpliceArray&lt;ColumnType&lt;any&gt;&gt;(defaultColumns)\n\n    const handleCancel = () =&gt; {\n        setImportModal(false)\n        setModalStep(0)\n            setExceptionCheck(false)\n            setExceptionContent(\'\')\n      }\n\n        const modalLoadTemplate = () =&gt; {\n            let a: any = document.createElement(\'a\')\n            document.body.appendChild(a)\n            a.style = \'display: none\'\n            getDemandStoreGetShelvesImportTemplate({}, { responseType: \'blob\', getResponse: true }).then((res: any) =&gt; {\n              const { response } = res\n              const filename = response.headers.get(\'content-disposition\').split(\'=\')[1]\n              let blob = new Blob([response.data], { type: \'application/vnd.ms-excel\' })\n              const url = window.URL.createObjectURL(blob)\n              a.href = url\n              a.download = filename.replaceAll(\'\"\', \'\')\n              a.click()\n              window.URL.revokeObjectURL(url)\n            })\n          }\n\n      const step0Description = (\n        &lt;&gt;\n          &lt;ul className={styles.step0Description}&gt;\n            &lt;li&gt;\n              点击下载 EXCEL文件模板{\' \'}\n                        &lt;a onClick={modalLoadTemplate}&gt;\n                            下载模板\n                        &lt;/a&gt;\n            &lt;/li&gt;\n            &lt;li&gt;按照模板整理器架信息资料&lt;/li&gt;\n            &lt;li&gt;点击导入按钮，导入整理好的器架信息资料&lt;/li&gt;\n            &lt;li&gt;单次最多导入1000条，文件大小不能超过10M&lt;/li&gt;\n          &lt;/ul&gt;\n        &lt;/&gt;\n      )\n\n\n          const step1Exception = (\n            &lt;div className={styles.step1Description}&gt;\n              &lt;h4&gt;存在错误格式数据，已生成错误日志&lt;/h4&gt;\n              &lt;p&gt;请导出错误日志修正数据后再次导入&lt;/p&gt;\n            &lt;/div&gt;\n          )\n\n            const download_txt = (filename: string, content: string, contentType?: string) =&gt; {\n                if (!contentType) contentType = \'application/octet-stream\'\n                var a = document.createElement(\'a\')\n                var blob = new Blob([content], { type: contentType })\n                a.href = window.URL.createObjectURL(blob)\n                a.download = filename\n                a.click()\n              }\n\n            const exportErrorLog = () =&gt; {\n                download_txt(\'log.txt\', exceptionContent)\n                setModalStep(0)\n                setImportModal(false)\n              }\n\n         const importProps = {\n            name: \'file\',\n            action: \'/api/demand/store/importStoreShelves\',\n            headers: {\n              Accesstoken: accessToken,\n            },\n            showUploadList: false,\n            maxCount: 1,\n            className: styles.importBtn,\n            onChange(info) {\n              setLoading(true)\n              if (info.file.status !== \'uploading\') {\n                console.log(info.file)\n              }\n              if (info.file.status === \'done\') {\n                console.log(info.file)\n                setModalStep(1)\n                setExceptionContent(info.file.response.message)\n                if (info.file.response.code === 1000) {\n                  setExceptionCheck(false)\n                  setStep1DescriptState(step1DescripSuccess)\n                            settdata(info.file.response.data)\n                } else {\n                  setExceptionContent(info.file.response.message)\n                  setExceptionCheck(true)\n                  setStep1DescriptState(step1Exception)\n                }\n                setLoading(false)\n              } else if (info.file.status === \'error\') {\n                setLoading(false)\n                console.log(info.file)\n              }\n            },\n          }\n\n            useEffect(() =&gt; {\n              currentRef.current = {\n                get: () =&gt;\n                  new Promise((resolve: any) =&gt; {\n                        resolve({\n                          state: true,\n                          name: \'equipment\',\n                          data: tdata,\n                        })\n                      .catch((error) =&gt; {\n                        if (error &amp;&amp; error.errorFields) {\n                        }\n                      })\n                  }),\n              }\n            },[tdata])\n\n            useEffect(() =&gt; {\n                if (fetchdata.length &gt; 0) {\n                    setTimeout(() =&gt; {\n                        settdata([...fetchdata])\n                        ref.current.reload()\n                    },0)\n                }\n            }, [fetchdata])\n\n  return (\n            &lt;&gt;\n      &lt;Card&gt;\n                &lt;Space&gt;\n                  &lt;Button onClick={() =&gt; setImportModal(true)}&gt;\n                    Excel导入\n                  &lt;/Button&gt;\n                &lt;/Space&gt;\n        &lt;StandardTable\n          columns={columns}\n          currentRef={ref}\n          fetchTableData={(params: any) =&gt; fetchData(params)}\n        /&gt;\n      &lt;/Card&gt;\n            &lt;Modal title=\'导入\' open={importModal} onCancel={handleCancel} maskClosable={false} footer={null}&gt;\n                    {modalStep === 0 &amp;&amp; (\n                      &lt;&gt;\n                        &lt;Result\n                          icon={&lt;FileExcelOutlined /&gt;}\n                          title={step0Description}\n                          extra={\n                            &lt;Upload {...importProps}&gt;\n                              &lt;Button style={{ width: \'100%\' }} type=\"primary\" loading={loading}&gt;\n                                上传\n                              &lt;/Button&gt;\n                            &lt;/Upload&gt;\n                          }\n                        /&gt;\n                      &lt;/&gt;\n                    )}\n                    {modalStep === 1 &amp;&amp; !exceptionCheck &amp;&amp; (\n                      &lt;&gt;\n                        &lt;Result icon={&lt;Progress type=\"circle\" percent={100} status=\"success\" /&gt;} title={step1DescriptState} /&gt;\n                      &lt;/&gt;\n                    )}\n                    {modalStep === 1 &amp;&amp; exceptionCheck &amp;&amp; (\n                      &lt;&gt;\n                        &lt;Result\n                          icon={&lt;Progress type=\"circle\" percent={100} status=\"exception\" /&gt;}\n                          title={step1Exception}\n                          extra={\n                            &lt;Button onClick={exportErrorLog} style={{ width: \'100%\' }}&gt;\n                              导出错误日志\n                            &lt;/Button&gt;\n                          }\n                        /&gt;\n                      &lt;/&gt;\n                    )}\n                  &lt;/Modal&gt;\n                    &lt;/&gt;\n  )\n\n}\n\nexport default Equipment\n</code></pre>', 0, 0, 0, 0, '2024-05-22 21:17:07', '2024-05-22 21:17:07', b'1', b'0');
INSERT INTO `club_post` VALUES (1953, 96, 20, '请问是否有这样的开源的库进行文本对比，返回哪里有增删改的信息呢？', '<p><img width=\"723\" height=\"341\" referrerpolicy=\"no-referrer\" src=\"/img/bVdcvcO\" alt=\"image.png\" title=\"image.png\"></p>\n<p>请问我想要对比纯文本文件的内容变化，<br>这个截图是在：<a href=\"https://link.segmentfault.com/?enc=9%2BheQVGJepTHD7torpR%2BSQ%3D%3D.xG4KEDm0PTmiWxRGbq3EY7vg5%2BAXdZ9RtwvtdIS6%2FkINdJ8JjrqgLPZUm5KTqzCb\" rel=\"nofollow\" target=\"_blank\">https://tools.wujingquan.com/textdiff/</a> <br>此在线工具检验的。</p>\n<p>我想要写一个方法，或者如果现有的开源库中有方法，可以传输2个文本（两个历史版本内容），可以进行对比，返回给哪些地方(行)有增删改的信息。</p>\n<p>请问是否有这样的开源的库呢？</p>', 0, 0, 0, 0, '2024-05-22 21:17:07', '2024-05-22 21:17:07', b'1', b'0');
INSERT INTO `club_post` VALUES (1954, 100, 20, '发送form-data数据时，boundary是浏览器自动设置的吗？', '<p>发送form-data数据时，boundary是浏览器自动设置的吗？可以手动设置吗？或者发送前能否获取boundary的值是什么？</p>\n<pre><code>$.ajax({\n      type:\"post\",\n      url:\"/login\",\n      data:formData,\n      success:function(res){\n      },\n      processData: false,\n      contentType: “multipart/form-data;boundary=xxx”,\n      error:function(xhr){\n          console.log(xhr)\n      }</code></pre>', 0, 0, 0, 0, '2024-05-22 21:17:08', '2024-05-22 21:17:08', b'1', b'0');
INSERT INTO `club_post` VALUES (1955, 117, 20, 'vue3的组件执行问题？', '<p>下面的代码直接打开这个页面执行没有问题，但是做为组件放到Dialog里被另一个页面执行，打开是图片就无放显示，有大神能看出是哪里问题吗？</p>\n<pre><code>&lt;script setup lang=\"tsx\"&gt;\nimport Canvas from \'@/views/Test/Canvas.vue\'\nimport { ContentWrap } from \'@/components/ContentWrap\'\nimport { onMounted, ref } from \'vue\'\nimport { fabric } from \'fabric\'\nconst imgUrl = ref(\'https://www.chunten.com/angpu/a1.jpg\')\nconst canvasAll = ref&lt;HTMLDivElement | null&gt;(null)\nlet canvas: fabric.Canvas | null = null\nonMounted(() =&gt; {\n  console.log(\'SetImage的onMounted执行了\')\n  if (canvasAll.value) {\n    canvas = new fabric.Canvas(\'canvas2\')\n    loadImg(imgUrl.value)\n  }\n})\nconst loadImg = (url) =&gt; {\n  console.log(\'loadImg执行了。。。\')\n  console.log(canvasAll.value)\n  if (canvasAll.value) {\n    canvas!.setWidth(canvasAll.value.$el.offsetWidth)\n    canvas!.setHeight(800)\n  }\n  fabric.Image.fromURL(url, (img) =&gt; {\n    img.left = 0\n    img.top = 10\n    canvas!.clear()\n    canvas!.add(img)\n    canvas?.zoomToPoint({ x: 0, y: 0 }, 1)\n    canvas!.renderAll()\n  })\n}\n&lt;/script&gt;\n\n&lt;template&gt;\n  &lt;ContentWrap ref=\"canvasAll\"&gt;\n    &lt;canvas id=\"canvas2\"&gt;&lt;/canvas&gt;\n  &lt;/ContentWrap&gt;\n&lt;/template&gt;\n\n&lt;style scoped lang=\"less\"&gt;\n.all {\n  width: 100%;\n  height: 700px;\n  background: #0f2438;\n}\n&lt;/style&gt;\n</code></pre>', 0, 0, 0, 0, '2024-05-22 21:17:09', '2024-05-22 21:17:09', b'1', b'0');
