INSERT INTO `club_post` VALUES (2340, 97, 20, '新手Java语法问题：字符转义相关疑问?', '<p>Java语法相关，什么情况下需要多打一个\"\\\"?</p>\n<pre><code>import java.util.Scanner;\nimport java.util.regex.Pattern;\n\npublic class Main {\n    public static void main(String[] args) {\n        String str;\n        Scanner sc = new Scanner(System.in);\n        str = sc.nextLine();\n        int index = str.indexOf(\'[\');\n        int lastIndex = str.lastIndexOf(\']\');\n        str = str.substring(index + 1, lastIndex);\n\n        String s = \"1\";\n\n        System.out.println(\"str:\" + str);\n        if (Pattern.matches(str, s))\n            System.out.println(\"str与s匹配成功！\");\n        else System.out.println(\"str与s匹配失败！\");\n\n        String str1 = \"\\\\d\";\n        System.out.println(\"str1:\" + str1);\n        if (Pattern.matches(str1, s))\n            System.out.println(\"str1与s匹配成功！\");\n        else System.out.println(\"str1与s匹配成功！\");\n\n        System.out.println(str1.equals(str));\n    }\n}</code></pre>\n<p><img width=\"556\" height=\"358\" referrerpolicy=\"no-referrer\" src=\"/img/bVdb4LN\" alt=\"我的尝试\" title=\"我的尝试\"></p>', 0, 0, 0, 0, '2024-05-22 21:21:15', '2024-05-22 21:21:15', b'1', b'0');
